mapper.select:
SELECT COUNT(id)
FROM forms
WHERE is_active = 1 AND name = #{name}
 AND id != #{id}

mapper.insert:
INSERT INTO sub_sub_forms (
    sub_form_id,
    name
) VALUES (
             #{subSubFormRequest.subFormId},
             #{subSubFormRequest.name}
         );

mapper.update:
UPDATE forms SET
 `name` = #{form.name},
 `form_type` = #{form.formType},
modified = NOW(),
modified_by = #{form.modifiedBy}
WHERE id = #{form.id}
